mapper.select:
select car_seq, fk_empid, car_num, car_type, max_num, insurance, drive_year, license, car_imgfilename, car_orgimgfilename
from tbl_car
where fk_empid = #{fk_empid}

select car_seq, fk_empid, car_num, car_type, max_num, insurance, drive_year, license, car_imgfilename, car_orgimgfilename
from tbl_car
where fk_empid = #{fk_empid}

mapper.update:
update tbl_car set car_num = #{car_num}


				 , car_orgimgfilename = #{car_orgimgfilename}




                 , car_imgfilename = #{car_imgfilename}


                 , car_type = #{car_type}
                 , max_num = #{max_num}
                 , drive_year = #{drive_year}
where fk_empid = #{fk_empid}

mapper.select:
select res_num, fk_car_seq, start_date,last_date,dp_name,dp_add,dp_lat, dp_lng, ds_name, ds_add, ds_lat, ds_lng, want_max, end_status, cancel_status, start_time
from tbl_day_share
where res_num = #{res_num}

select res_num, start_date, last_date, dp_name, ds_name, want_max, end_status, cancel_status, start_time, nickname, readCount from ( select row_number() over(order by res_num desc) AS rno, res_num, start_date, last_date, dp_name, ds_name, want_max, end_status, cancel_status, start_time, nickname, readCount from ( select res_num, start_date, last_date, dp_name, ds_name, want_max, end_status, cancel_status, start_time, fk_car_seq, readCount from tbl_day_share where end_status = 1 and cancel_status = 1
and lower(dp_name) like '%'|| lower(#{searchWord})||'%'
and lower(ds_name) like '%'|| lower(#{searchWord})||'%'
AND TO_DATE(#{start}, 'YYYY-MM-DD') BETWEEN start_date AND last_date
)a cross join ( select car_seq, fk_empid, car_num, car_type, max_num, nickname from ( select * from tbl_car ) v cross join ( select * from tbl_employees ) h where fk_empid = h.empid )b where fk_car_seq = b.car_seq )C WHERE c.rno between #{startRno} and #{endRno}

select res_num, start_date, last_date, dp_name, ds_name, want_max, end_status, cancel_status, start_time, nickname, readCount from ( select row_number() over(order by res_num desc) AS rno, res_num, start_date, last_date, dp_name, ds_name, want_max, end_status, cancel_status, start_time, nickname, readCount from ( select res_num, start_date, last_date, dp_name, ds_name, want_max, end_status, cancel_status, start_time, fk_car_seq, readCount from tbl_day_share where end_status = 1 and cancel_status = 1
and lower(dp_name) like '%'|| lower(#{searchWord})||'%'
and lower(ds_name) like '%'|| lower(#{searchWord})||'%'
AND TO_DATE(#{start}, 'YYYY-MM-DD') BETWEEN start_date AND last_date
)a cross join ( select car_seq, fk_empid, car_num, car_type, max_num, nickname from ( select * from tbl_car ) v cross join ( select * from tbl_employees where empid=#{empid} ) h where fk_empid = h.empid )b where fk_car_seq = b.car_seq )C WHERE c.rno between #{startRno} and #{endRno}
